import "./ExpenseAll.css";
import ExpenseItem from "./ExpenseItem.js";
import Card from "../UI/Card.js";
import ExpensesFilter from "./ExpenseFilter";
import { useState } from "react";

function ExpenseAll(props) {
  const [filteredYear, setFilteredYear] = useState("2022");
  const selectFilterHanler = (selectedYear) => {
    console.log(selectedYear);
  };

  const filteredExpenses =
    props.items /
    filter((expense) => expense.date.getFullYear().toString() === filteredYear);
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onSelectFilter={selectFilterHanler}
        ></ExpensesFilter>
        {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default ExpenseAll;
