import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";
import { useState } from "react";

const NewExpense = function (props) {
  const [isAddingNewExpense, setIsAddingNewExpense] = useState(false);
  const saveExpenseDataHanlder = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);
  };

  return (
    <div className="new-expense">
      {!isAddingNewExpense && <button>Add new expense</button>}
      {isAddingNewExpense && (
        <ExpenseForm onSaveExpenseData={saveExpenseDataHanlder}></ExpenseForm>
      )}
    </div>
  );
};

export default NewExpense;
